using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using DataLayer;using DataTransferObject;namespace BusinessLayer{    public class UserServices    {        User user { get; set; }        public UserDTO checkLoginUser(UserDTO userDTO)        {            using (ProjectManagementSystemDBContext dbContext = new ProjectManagementSystemDBContext())            {                var user = dbContext.Users.FirstOrDefault(u => u.Username == userDTO.Username);                if (user == null)                    return null;                else                {                    if (user.PasswordHash != userDTO.PasswordHash)                        return null;                    else                        return new UserDTO                        {                            UserID = user.UserID,                            Username = user.Username,                            Email = user.Email,                            FirstName = user.FirstName,                            LastName = user.LastName,                            RoleID = user.RoleID,                            Avatar = user.Avatar                            //PhoneNumber = user.PhoneNumber,                            //Address = user.Address,                        };    }            }        }    }}